import React, { Component } from 'react'

var gridStyle = (n)=>{
    return {
        display:"grid",
        gridTemplateColumns: "repeat("+n+", 1fr)",
        gridTemplateRows:"repeat("+n+", 1fr)",
        height: 500,
        width: 500,
        margin: "30px auto",
    }
}

var cellStyle = (n, num)=>{
    var row = parseInt(n/this.props.num)
    var col = n%row
    console.log(n,row,col)
    
    var val2col = ["red", "yellow", "green"]
    
    var val = n
    return {
        border: "1px solid lightgrey",
        margin: 0,
        padding: 0,
        backgroundColor: val2col[val%val2col.length]

    }
}

export default class Viz extends Component {
    constructor(props){
        super(props);
        this.state = {
            
        }
        this.grid = []
        for(var i=0; i<this.props.num*this.props.num; i++){
            this.grid.push(<div>{i}</div>)
        }
    }
    
    shouldComponentUpdate(nextProps, nextState){
        // console.log(gridStyle(5));
        if(parseInt(nextProps.num)!=parseInt(this.props.num)){
            this.grid = []
            for(var i=0; i<nextProps.num*nextProps.num; i++){
                this.grid.push(<div style={cellStyle(i, this.props.num)}>{i}</div>)
            }
            return true;
        }
        return false;
        
    }

    // componentDidUpdate(props){
    //     this.x = [];
    //     for(var i=0; i<this.props.num; i++){
    //         this.x.push(<div>i</div>)
    //     }
    //     console.log(this.x);
    //     this.render()
    // }

    render() {
        return (
            <div id="grid" style={gridStyle(this.props.num)}>
                {this.grid}
            </div>
        )
    }
}
