import React, { Component } from 'react'

export default class Control extends Component {
    constructor(props){
        super(props);
        this.state = {
            num: 4,
        }
    }

    updateNum(e){
        setState({num:e.target.value});
        // console.log(e.target.value)
    }


    render(){
        return(
            <div>
                <form>
                    <label>
                    Number of Items:
                    <input type="range" name="num" min="4" max="10" step="2" value={this.state.num} onChange={this.updateNum}/>
                    </label>

                </form>



            </div> 
        )
    }
}
